import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent {
  showHome = false;
  showSites = false;

  handleSites(flag: boolean) {
    this.showSites = true;
    this.showHome = false;
  }

  handleHome(flag: boolean) {
    this.showSites = false;
    this.showHome = true;
  }
}
<main class="main-container">
  <div class="container">
    <nav class="navbar">
      <div class="navdiv">
        <div class="la">
          <div class="ic">
            <div class="logo">
              <img src="../../../../assets/images/nrg-logo.png" alt="logo" />
            </div>

            <div class="app">VHOS</div>
            <span class="version">Angular 16</span>
          </div>
        </div>
      </div>
    </nav>
  </div>

  <div class="auto-scroll tab-container">
    <app-site-list [show]="showSites" (toggleSite)="handleSites($event)" />
    <app-home [show]="showHome" (toggleHomeView)="handleHome($event)" />
  </div>
</main>
